    public partial class frmWebInput : Form
    {
        public static HttpListener listener;
        Thread t;
        string status = "off";
        bool _exit = false;

        /* Port Number */
        string port = "61337";

        public frmWebInput()
        {
            InitializeComponent();
            this.Resize += new EventHandler(frmWebInput_Resize);
            this.FormClosing += new System.Windows.Forms.FormClosingEventHandler(this.frmWebInput_FormClosing);

        }
        
        /* Turns the service on/off
         */
        private void btnSwitch_Click(object sender, EventArgs e)
        {
            if (status == "off")
            {
                try
                {
                    listener = new HttpListener();
                    listener.Start();
                    listener.Prefixes.Add("http://+:" + port + "/");
                    t = new Thread(new ThreadStart(clientListener));
                    t.Start();

                    lblServerStatus.Text = "On";
                    btnSwitch.Text = "Turn Off";
                    status = "on";

                    lblIP.Text = "http://" + LocalIPAddress() + ":" + port;
                    lblIP.Visible = true;
                }
                catch (HttpListenerException Ex)
                {
                    Console.WriteLine(Ex);

                    lblIP.Text = "Could not start service.";
                    lblIP.Visible = true;
                }
            }

            else
            {
                listener.Abort();
                t.Abort();
                lblServerStatus.Text = "Off";
                btnSwitch.Text = "Turn On";
                status = "off";

                lblIP.Visible = false;
            }
        }

        /* Get the local IP
         * Thanks to Mrchief @
         * http://stackoverflow.com/questions/6803073/get-local-ip-address-c-sharp
         */
        public string LocalIPAddress()
        {
            IPHostEntry host;
            string localIP = "";
            host = Dns.GetHostEntry(Dns.GetHostName());
            foreach (IPAddress ip in host.AddressList)
            {
                if (ip.AddressFamily == AddressFamily.InterNetwork)
                {
                    localIP = ip.ToString();
                }
            }
            return localIP;
        }

        /* Listen for Web Server Requests
         */
        public static void clientListener()
        {
            while (true)
            {
                try
                {
                    HttpListenerContext request = listener.GetContext();
                    ThreadPool.QueueUserWorkItem(processRequest, request);
                }
                catch (Exception e) {  }
            }
        }

        /* Process the Web Server Requests
         */
        public static void processRequest(object listenerContext)
        {
            try
            {
                string _output = "";
                var context = (HttpListenerContext)listenerContext;
                string filename = Path.GetFileName(context.Request.RawUrl);

                /* Show Remote */
                if ( filename == "" )
                {
                    _output = htmlRemote();
                }

                /* Process Touchpad */
                else if (filename.Contains("remote-touch"))
                {
                    string[] piecesA = filename.Split(new string[] { "x=" }, StringSplitOptions.None);
                    string[] piecesB = piecesA[1].Split(new string[] { "&y=" }, StringSplitOptions.None);

                    // Divide the numbers, to slow down movement
                        int x = (Convert.ToInt32(piecesB[0]) * -1)/5;
                        int y = (Convert.ToInt32(piecesB[1]) * -1)/5;

                        if (x == 0 && y == 0)
                        {
                            /* Process Left Click */
                                VirtualMouse.LeftClick();
                        }

                        else
                        {
                            /* Move the mouse cursor X, Y pixels */
                                VirtualMouse.Move(x, y);
                        }
                }
                
                /* Process Mouse Click */
                else if (filename.Contains("process-click"))
                {
                    string[] piecesA = filename.Split(new string[] { "button=" }, StringSplitOptions.None);

                    if ( piecesA[1] == "left" )
                    {
                        VirtualMouse.LeftClick();
                    }

                    else if (piecesA[1] == "right")
                    {
                        VirtualMouse.RightClick();
                    }

                    else
                    {

                    }
                }

                /* Send Keystrokes */
                else if (filename.Contains("process-text"))
                {
                    /* Passing Text through POST Data
                        * Needs work:
                        * Does not handle spaces properly
                        * Does not handle new lines properly
                     */
                    string text;
                    using (var reader = new StreamReader(context.Request.InputStream,
                                                         context.Request.ContentEncoding))
                    {
                        text = reader.ReadToEnd();
                    }

                    string[] piecesA = text.Split(new string[] { "text=" }, StringSplitOptions.None);
                    piecesA[1].Replace(" ", "{SPACE}");

                    SendKeys.SendWait(piecesA[1]);
                }

                /* Arrow Keys */
                else if (filename.Contains("process-button"))
                {
                    string[] piecesA = filename.Split(new string[] { "button=" }, StringSplitOptions.None);

                    switch (piecesA[1])
                    {
                        case "up":
                            SendKeys.SendWait("{UP}");
                        break;

                        case "right":
                            SendKeys.SendWait("{RIGHT}");
                        break;

                        case "down":
                            SendKeys.SendWait("{DOWN}");
                        break;

                        case "left":
                            SendKeys.SendWait("{LEFT}");
                        break;

                        default:
                            break;
                    }
                }

                /* Unknown Handler */
                else
                {
                    _output = "<html><a href='http://theyconfuse.me/c'>TheyConfuse.Me</a></html>";
                }

                
                byte[] array = Encoding.ASCII.GetBytes(_output);
                context.Response.ContentLength64 = array.Length;
                using (Stream s = context.Response.OutputStream)
                    s.Write(array, 0, array.Length);
            }
            catch
            {

            }
        }

        /* Exit the Application
         */
        private void btnExit_Click(object sender, EventArgs e)
        {
            _exit = true;
            Application.Exit();
        }

        /* Open Credit Link
         */
        private void linkLabel1_LinkClicked(object sender, LinkLabelLinkClickedEventArgs e)
        {
            System.Diagnostics.Process.Start("http://theyconfuse.me");
        }

        /* Form Resize Handler
         */
        private void frmWebInput_Resize(object sender, EventArgs e)
        {
            if (FormWindowState.Minimized == this.WindowState)
            {
                notifyIcon1.Visible = true;
                this.Hide();    
            }
            else if (FormWindowState.Normal == this.WindowState)
            {
                notifyIcon1.Visible = false;
            }
        }

        /* Open the application if the icon is clicked
         * from the system tray
         */
        private void notifyIcon1_MouseDoubleClick_1(object sender, MouseEventArgs e)
        {
            this.Show();
            this.WindowState = FormWindowState.Normal;
        }

        /* Form closing Handler
         */
        private void frmWebInput_FormClosing(object sender, FormClosingEventArgs e)
        {
            if (_exit == true)
            {
                Application.Exit();
            }
            else
            {
                e.Cancel = true;
                this.WindowState = FormWindowState.Minimized;
            }

        }

